      supportedParameters: [
        'temperature', 
        'maxTokens', 
        'top_p', 
        'presence_penalty', 
        'frequency_penalty',
        'systemMessage',
        'n',
        'stop'
      ],
      maxContextLength: 128000, // GPT-4 context length
      supportsStreaming: true,
